import React, { useState, useEffect, useRef } from 'react';
import { request } from '../../../axios_helper';
import FiltersForm from '../FiltersForm';
import { exportToXML } from '../../../helpers/saveToXML';
import { exportToJSON } from '../../../helpers/saveToJSON';
import PricesChart from './PricesChart';
import AmountsChart from '../amounts/AmountsChart';

const PricesData = () => {
    const [pricesData, setPricesData] = useState([]);
    const [numbersData, setNumbersData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filters, setFilters] = useState({
        year: '',
        region: '',
        surface: '',
        before: '',
        after: '',
        sort: '',
        sortAsc: '',
        oneYear: ''
    });

    const [filtersSubmitted, setFiltersSubmitted] = useState(false);
    const [showChart, setShowChart] = useState(false);
    const pricesChartRef = useRef(null);
    const amountsChartRef = useRef(null);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        const queryParams = new URLSearchParams();
        Object.keys(filters).forEach(key => {
            if (filters[key]) {
                queryParams.append(key, filters[key]);
            }
        });

        setLoading(true);
        setError(null);

        Promise.all([
            request('GET', `/api/prices?${queryParams}`),
            request('GET', `/api/amounts?${queryParams}`)
        ]).then(([
            pricesResponse,
            numbersResponse
        ]) => {
            setPricesData(pricesResponse.data);
            setNumbersData(numbersResponse.data);
            setLoading(false);
        }).catch(error => {
            console.log('Error:', error);
            setError(error.message);
            setLoading(false);
        });
    };

    const handleExportToXML = () => {
        const xmlData = pricesData.map(item => (
            `<item>
                <region>${item.region}</region>
                <quarter>${item.quarter}</quarter>
                <type>${item.type}</type>
                <surface>${item.surface}</surface>
                <year>${item.year}</year>
                <price>${item.price}</price>
            </item>`
        )).join('');

        exportToXML(`<data>${xmlData}</data>`, 'filtered_data');
    };

    const handleExportToJSON = () => {
        exportToJSON(pricesData, 'filtered_data');
    };

    const handleDownloadChart = () => {
        if (pricesChartRef.current) {
            pricesChartRef.current.downloadChart();
        }
    };

    const handleFilterReset = () => {
        setFiltersSubmitted(false);
        setShowChart(false);
    };

    if (loading) {
        return <div>Ładowanie danych...</div>;
    }

    if (error) {
        return <div>Błąd: {error}</div>;
    }

    return (
        <div>
            <FiltersForm
                filters={filters}
                setFilters={setFilters}
                onFilterSubmit={() => setFiltersSubmitted(true)}
                fetchData={fetchData}
                onFilterReset={handleFilterReset}
                sortLabel="Sortowanie po cenie rosnąco"
            />
            <div className="form-container">
                <p className="lead">Eksport danych</p>
                <div className="row justify-content-start">
                    <div className="col-6">
                        <button className="submit-button full-width" onClick={handleExportToXML}>Pobierz dane w formacje XML</button>
                    </div>
                    <div className="col-6">
                        <button className="submit-button full-width" onClick={handleExportToJSON}>Pobierz dane w formacie JSON</button>
                    </div>
                </div>
                <p></p>
                {filtersSubmitted && (
                    <div className="row justify-content-start">
                        <div className="col-6">
                            <button className="submit-button violet-btn full-width" onClick={() => setShowChart(!showChart)}>
                                {showChart ? 'Ukryj wykres' : 'Pokaż wykres'}
                            </button>
                        </div>
                        {showChart && (
                            <div className="col-6">
                                <button className="submit-button violet-btn full-width" onClick={handleDownloadChart}>
                                    Pobierz wykres
                                </button>
                            </div>
                        )}
                    </div>
                )}
            </div>
            <div className="container">
                <div class="row">
                    <div class="col">
                    {showChart && <PricesChart data={pricesData} ref={pricesChartRef} />}
                    </div>
                    <div class="col">
                    {showChart && <AmountsChart data={numbersData} ref={amountsChartRef} />}

                    </div>
                </div>
                {showChart && <PricesChart data={pricesData} ref={pricesChartRef} />}
                <p></p>
                {showChart && <AmountsChart data={numbersData} ref={amountsChartRef} />}
                <table className="table">
                    <thead>
                        <tr>
                            <th>Region</th>
                            <th>Rok</th>
                            <th>Kwartał</th>
                            <th>Typ mieszkania</th>
                            <th>Powierzchnia</th>
                            <th>Średnia cena (zł)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pricesData.map(item => (
                            <tr key={item.id}>
                                <td>{item.region}</td>
                                <td>{item.year}</td>
                                <td>{item.quarter}</td>
                                <td>{item.type}</td>
                                <td>{item.surface}</td>
                                <td>{item.price}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default PricesData;
