import React from 'react';
import { withRouter } from 'react-router-dom';

class LoginForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            active: "login",
            login: "",
            password: "",
            onLogin: props.onLogin,
            onRegister: props.onRegister
        };
    }

    static contextType = withRouter;

    onChangeHandler = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        this.setState({ [name]: value });
    }

    onSubmitLogin = (e) => {
        e.preventDefault();
        request(
            "POST",
            "/api/v1/auth/login",
            {
                login: this.state.login,
                password: this.state.password
            }).then(
                (response) => {
                    localStorage.setItem('token', response.data.token);
                    setAuthHeader(localStorage.getItem('token'));
                    this.props.onLogin(null, this.state.login, this.state.password);
                    this.props.history.push('/welcome');
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "loginerror" });
                    }
                );
    }

    onSubmitRegister = (e) => {
        e.preventDefault();
        request(
            "POST",
            "/api/v1/auth/register",
            {
                login: this.state.login,
                password: this.state.password
            }).then(
                (response) => {
                    localStorage.setItem('token', response.data.token);
                    setAuthHeader(localStorage.getItem('token'));
                    this.setState({ componentToShow: "welcome" });
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "registererror" });
                    }
                );
    }

    render() {
        const isLoginActive = this.state.active === "login";
        const submitButtonText = isLoginActive ? "Zaloguj się" : "Zarejestruj się";

        return (
            <div className="row justify-content-center">
                <div className="col-4">
                    {/* reszta Twojego renderowania */}
                </div>
            </div>
        );
    }
}

export default LoginForm;
