import React, { useState, useEffect } from 'react';
import { request } from '../axios_helper';

const DataTable = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filters, setFilters] = useState({
        year: '',
        price: '',
        region: '',
        surface: '',
        type: '',
        quarter: '',
        before: '',
        sort: ''
    });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        const queryParams = new URLSearchParams(filters).toString();
        request('GET', `/api/prices?${queryParams}`)
            .then(response => {
                setData(response.data);
                setLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setLoading(false);
            });
    };

    const handleFilterChange = (event) => {
        const { name, value } = event.target;
        setFilters({ ...filters, [name]: value });
    };

    const filteredData = data.filter(item => {
        for (const key in filters) {
            if (filters[key] && item[key] !== filters[key]) {
                return false;
            }
        }
        return true;
    });

    if (loading) {
        return <div>Ładowanie danych...</div>;
    }

    if (error) {
        return <div>Błąd: {error}</div>;
    }

    return (
        <div>
            <form>
                <label>
                    Sortowanie po cenie rosnąco:
                    <input type="checkbox" name="sort" value={filters.sort} onChange={handleFilterChange} />
                </label>
                <button type="submit">Filtruj</button>
            </form>
            <div className="container">

            <table className="table">
                <thead>
                    <tr>
                        <th>Region</th>
                        <th>Kwartał</th>
                        <th>Typ mieszkania</th>
                        <th>Powierzchnia</th>
                        <th>Rok</th>
                        <th>Średnia cena</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredData.map(item => (
                        <tr key={`${item.region}-${item.quarter}-${item.year}`}>
                            <td>{item.region}</td>
                            <td>{item.quarter}</td>
                            <td>{item.type}</td>
                            <td>{item.surface}</td>
                            <td>{item.year}</td>
                            <td>{item.price}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            </div>
        </div>
    );
};

export default DataTable;

