import React, { useState, useEffect } from 'react';
import { request } from '../axios_helper';
import FiltersForm from './FiltersForm';
import { exportToXML } from '../helpers/saveToXML';
import { exportToJSON } from '../helpers/saveToJSON';
import ChartComponent from './ChartComponent';

const DataTable = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filters, setFilters] = useState({
        year: '',
        price: '',
        region: '',
        surface: '',
        type: '',
        before: '', //0
        after: '', //0
        sort: '',
        sortAsc: '',
        oneYear: '' //0
    });
    const wojewodztwa = [
        'DOLNOŚLĄSKIE', 'KUJAWSKO-POMORSKIE', 'LUBELSKIE', 'LUBUSKIE',
        'ŁÓDZKIE', 'MAŁOPOLSKIE', 'MAZOWIECKIE', 'OPOLSKIE',
        'PODKARPACKIE', 'PODLASKIE', 'POMORSKIE', 'ŚLĄSKIE',
        'ŚWIĘTOKRZYSKIE', 'WARMIŃSKO-MAZURSKIE', 'WIELKOPOLSKIE', 'ZACHODNIOPOMORSKIE'
    ];

    const lata = Array.from({ length: 2022 - 2012 + 1 }, (_, i) => 2012 + i);

    const powierzchnie = [
        'do 40 m2', 'od 40,1 do 60 m2', 'od 60,1 do 80 m2', 'od 80,1 m2'
    ];

    const [filtersSubmitted, setFiltersSubmitted] = useState(false);
    const [showChart, setShowChart] = useState(false);
    const chartRef = useRef(null);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        const queryParams = new URLSearchParams();
        Object.keys(filters).forEach(key => {
            if (filters[key]) {
                queryParams.append(key, filters[key]);
            }
        });
        request('GET', `/api/prices?${queryParams}`)
            .then(response => {
                setData(response.data);
                setLoading(false);

            })
            .catch(error => {
                console.log('Error:', error);
                setError(error.message);
                setLoading(false);
            });
    };

    const handleFilterChange = (event) => {
        const { name, value } = event.target;
        setFilters({ ...filters, [name]: value });
    };

    const handleRadioChange = (event) => {
        const { name, value } = event.target;
        setFilters({ ...filters, [name]: value, before: '0', after: '0', oneYear: '0', [name]: value });
    };

    const handleRadioClick = (event) => {
        const { name } = event.target;
        if (filters[name]) {
            setFilters({ ...filters, [name]: '' });
        }
    };
    const handleFilterSubmit = (event) => {
        event.preventDefault();
        setLoading(true);
        fetchData();
        setFiltersSubmitted(true);
    };

    const handleExportToXML = () => {
        const xmlData = data.map(item => (
            `<item>
                <region>${item.region}</region>
                <quarter>${item.quarter}</quarter>
                <type>${item.type}</type>
                <surface>${item.surface}</surface>
                <year>${item.year}</year>
                <price>${item.price}</price>
            </item>
            `
        )).join('');

        exportToXML(`<data>${xmlData}</data>`, 'filtered_data');
    };


    const handleExportToJSON = () => {
        exportToJSON(data, 'filtered_data');
    };

    const handleDownloadChart = () => {
        if (chartRef.current) {
            chartRef.current.downloadChart();
        }
    };

    if (loading) {
        return <div>Ładowanie danych...</div>;
    }

    if (error) {
        return <div>Błąd: {error}</div>;
    }

    const anyFiltersActive = Object.values(filters).some(value => value !== '');


    return (
        <div>

            <FiltersForm
                filters={filters}
                handleFilterChange={handleFilterChange}
                handleFilterSubmit={handleFilterSubmit}
                handleRadioChange={handleRadioChange}
                handleRadioClick={handleRadioClick}
                wojewodztwa={wojewodztwa}
                powierzchnie={powierzchnie}
                lata={lata}
            />
            <p></p>
            <div className="form-container">
                <p className="lead">Eksport danych</p>
                <div class="row justify-content-start">
                    <div class="col-6"> <button className="submit-button full-width" onClick={handleExportToXML}>Pobierz dane w formacje XML</button></div>
                    <div class="col-6"><button className="submit-button full-width" onClick={handleExportToJSON}>Pobierz dane w formacie JSON</button></div>
                </div>
                <p></p>
                {filtersSubmitted && (
                    <div>
                        <button className="submit-button violet-btn full-width" onClick={() => setShowChart(!showChart)}>
                            {showChart ? 'Ukryj wykres' : 'Pokaż wykres'}
                        </button>

                    </div>
                )}
            </div>


            {showChart && <ChartComponent data={data} />}
            {showChart && (
                <div>
                    <ChartComponent ref={chartRef} data={data} />
                    <button className="submit-button full-width" onClick={handleDownloadChart}>
                        Pobierz wykres
                    </button>
                </div>
            )}
            <p></p>
            <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Region</th>
                            <th>Kwartał</th>
                            <th>Typ mieszkania</th>
                            <th>Powierzchnia</th>
                            <th>Rok</th>
                            <th>Średnia cena (zł)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map(item => (
                            <tr key={`${item.id}`}>
                                <td>{item.region}</td>
                                <td>{item.quarter}</td>
                                <td>{item.type}</td>
                                <td>{item.surface}</td>
                                <td>{item.year}</td>
                                <td>{item.price}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );

};

export default DataTable;
