import React, { useEffect, useState } from 'react';
import Chart from 'chart.js/auto';

const ChartComponent = ({ data }) => {
    useEffect(() => {
        if (data.length > 0) {
            // Tworzenie unikalnej listy lat i kwartałów
            const years = [...new Set(data.map(item => item.year))];
            const quarters = [1, 2, 3, 4];

            // Tworzenie punktów danych na wykresie
            const chartData = years.reduce((acc, year) => {
                quarters.forEach(quarter => {
                    const quarterData = data.find(item => item.year === year && item.quarter === quarter);
                    console.log("qdata:", quarterData);

                    if (quarterData) {
                        acc.push({
                            x: `${year} Q${quarter}`,
                            y: quarterData.price,
                            region: quarterData.region,
                            surface: quarterData.surface
                        });
                    } else {
                        // Jeśli brak danych dla danego kwartału, ustaw cenę na null
                        acc.push({ x: `${year} Q${quarter}`, y: null });
                    }
                });
                return acc;
            }, []);
            console.log("ChartData:", chartData);

            const ctx = document.getElementById('myChart').getContext('2d');
            new Chart(ctx, {
                type: 'line',
                data: {
                    datasets: [{
                        label: 'Średnia cena',
                        data: chartData,
                        backgroundColor: 'rgba(75, 192, 192, 0.5)',
                        borderColor: 'rgb(75, 192, 192)',
                        pointRadius: 5,
                        pointHoverRadius: 8,
                        showLine: true
                    }]
                },
                options: {
                    scales: {
                        x: {
                            title: {
                                display: true,
                                text: 'Rok i kwartał'
                            }
                        },
                        y: {
                            title: {
                                display: true,
                                text: 'Średnia cena'
                            },
                            beginAtZero: true
                        }
                    },
                    plugins: {
                        tooltip: {
                            callbacks: {
                                label: function (context) {
                                    const dataPoint = context.parsed;
                                    if (dataPoint.y !== null) {
                                        return `Region: ${dataPoint.region}, Powierzchnia: ${dataPoint.surface}, Cena: ${dataPoint.y}`;
                                    } else {
                                        return `Brak danych`;
                                    }
                                }
                            }
                        }
                    }
                }
            });
        }
    }, [data]);

    return (
        <div>
            <canvas id="myChart" width="400" height="200"></canvas>
        </div>
    );
};

export default ChartComponent;
