import React, { useEffect, useState } from 'react';
import Chart from 'chart.js/auto';

const ChartComponent = ({ data }) => {
    console.log("tera")
    useEffect(() => {
        if (data.length > 0) {
            const primaryMarketData = data.filter(item => item.type === 'rynek pierwotny');
            const secondaryMarketData = data.filter(item => item.type === 'rynek wtórny');

            const regions = [...new Set(data.map(item => item.region))];

            const primaryMarketChartData = regions.reduce((acc, region) => {
                const regionData = primaryMarketData.filter(item => item.region === region);
                acc[region] = regionData.map(item => ({ x: `${item.year} Q${item.quarter}`, y: item.price, surface: item.surface }));
                return acc;
            }, {});

            const secondaryMarketChartData = regions.reduce((acc, region) => {
                const regionData = secondaryMarketData.filter(item => item.region === region);
                acc[region] = regionData.map(item => ({ x: `${item.year} Q${item.quarter}`, y: item.price, surface: item.surface }));
                return acc;
            }, {});

            console.log(secondaryMarketChartData)
            const ctx = document.getElementById('myChart').getContext('2d');
            const existingChart = Chart.getChart(ctx);
            if (existingChart) {
                existingChart.destroy();
            }

            const datasets = regions.map(region => {
                return {
                    label: `${region} - rynek wtórny`,
                    data: secondaryMarketChartData[region],
                    backgroundColor: 'rgba(54, 162, 235, 0.5)', // Kolor dla mieszkań z rynku wtórnego
                    borderColor: 'rgba(54, 162, 235, 1)',
                    borderWidth: 1,
                    pointRadius: 5,
                    pointHoverRadius: 8,
                    showLine: true
                };
            });


            new Chart(ctx, {
                type: 'line',
                data: {
                    datasets: datasets
                },
                options: {
                    scales: {
                        x: {
                            title: {
                                display: true,
                                text: 'Rok i kwartał'
                            }
                        },
                        y: {
                            title: {
                                display: true,
                                text: 'Średnia cena'
                            },
                            beginAtZero: true
                        }
                    },
                    plugins: {
                        tooltip: {
                            callbacks: {
                                label: function (context) {
                                    const dataPoint = context.parsed;
                                    console.log(dataPoint)
                                    return `Powierzchnia: ${dataPoint.surface}, Cena: ${dataPoint.y}`;
                                }
                            }
                        }
                    }
                }
            });
        }
    }, [data]);

    return (
        <div>
            <canvas id="myChart" width="400" height="200"></canvas>
        </div>
    );
};

export default ChartComponent;
