import * as React from 'react';
import logo from './house_black.svg';
import Buttons from './Buttons';
import { request, setAuthHeader } from '../../axios_helper';
// import LoginErrorContent from './auth/LoginErrorContent';
// import RegisterErrorContent from './auth/RegisterErrorContent';
import LoginForm from '../auth/LoginForm';

export default class Navbar extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            componentToShow: "welcome"
        }
    };

    login = () => {
        this.setState({ componentToShow: "login" });
    };

    logout = () => {
        this.setState({ componentToShow: "welcome" });
        setAuthHeader(null);
        localStorage.removeItem('token');
    };

    onLogin = (e, username, password) => {
        e.preventDefault();
        request(
            "POST",
            "/api/v1/auth/login",
            {
                login: username,
                password: password
            }).then(
                (response) => {
                    localStorage.setItem('token', response.data.token);
                    setAuthHeader(localStorage.getItem('token'));
                    this.setState({ componentToShow: "welcome" });
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "loginerror" });
                    }
                );
    };

    onRegister = (event, username, password) => {
        event.preventDefault();
        request(
            "POST",
            "/api/v1/auth/register",
            {
                login: username,
                password: password
            }).then(
                (response) => {
                    setAuthHeader(response.data.token);
                    this.setState({ componentToShow: "welcome" });
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "registererror" });
                    }
                );
    };


    render() {
        return (
            <nav class="navbar navbar-expand-lg navbar-light bg-light" >
                <div class="container-fluid">
                    <a class="navbar-brand" href="/">
                        <img src={logo} alt="" width="30" height="24" class="d-inline-block align-text-top" />
                    </a>
                    <div class="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul class="navbar-nav me-auto mb-2 mb-lg-0">

                            <li class="nav-item">
                                <a class="nav-link active" href="/prices">Ceny mieszkań</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link active" href="/amounts">Liczba oddanych mieszkań</a>
                            </li>
                        </ul>
                        <div class="d-flex">
                            <Buttons login={this.login} logout={this.logout} />
                        </div>
                    </div>
                </div>
            </nav>
        )
    }
}
