import React, { useState, useEffect, useRef } from 'react';
import { request } from '../../../axios_helper';
import FiltersForm from '../FiltersForm';
import { exportToXML } from '../../../helpers/saveToXML';
import { exportToJSON } from '../../../helpers/saveToJSON';
import DataChart from '../../view/DataChart';

const PricesData = () => {
    const [pricesData, setPricesData] = useState([]);
    const [numbersData, setNumbersData] = useState([]);
    const [combinedData, setCombinedData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filters, setFilters] = useState({
        year: '',
        region: '',
        surface: '',
        before: '',
        after: '',
        sort: '',
        sortAsc: '',
        oneYear: ''
    });

    const [filtersSubmitted, setFiltersSubmitted] = useState(false);
    const [showChart, setShowChart] = useState(false);
    const pricesChartRef = useRef(null);
    const amountsChartRef = useRef(null);

    useEffect(() => {
        fetchData();
    }, []);

    useEffect(() => {
        combineData();
    }, [pricesData, numbersData]);

    const fetchData = () => {
        const queryParams = new URLSearchParams();
        Object.keys(filters).forEach(key => {
            if (filters[key]) {
                queryParams.append(key, filters[key]);
            }
        });

        setLoading(true);
        setError(null);

        Promise.all([
            request('GET', `/api/prices?${queryParams}`),
            request('GET', `/api/amounts?${queryParams}`)
        ]).then(([
            pricesResponse,
            numbersResponse
        ]) => {
            setPricesData(pricesResponse.data);
            setNumbersData(numbersResponse.data);
            setLoading(false);
        }).catch(error => {
            console.log('Error:', error);
            setError(error.message);
            setLoading(false);
        });
    };

    const combineData = () => {
        console.log("teraz")
        const combined = pricesData.map(priceItem => {
            const matchingNumberItem = numbersData.find(numberItem => numberItem.id === priceItem.id);
            return {
                ...priceItem,
                amount: matchingNumberItem ? matchingNumberItem.number : 'N/A'
            };
        });
        setCombinedData(combined);
        console.log(combinedData)
    };

    const handleExportToXML = () => {
        const xmlData = combinedData.map(item => (
            `<item>
                <region>${item.region}</region>
                <quarter>${item.quarter}</quarter>
                <type>${item.type}</type>
                <surface>${item.surface}</surface>
                <year>${item.year}</year>
                <price>${item.price}</price>
                <amount>${item.amount}</amount>
            </item>`
        )).join('');

        exportToXML(`<data>${xmlData}</data>`, 'filtered_data');
    };

    const handleExportToJSON = () => {
        exportToJSON(combinedData, 'filtered_data');
    };

    const handleDownloadChart = () => {
        if (pricesChartRef.current) {
            pricesChartRef.current.downloadChart();
        }
        if (amountsChartRef.current) {
            amountsChartRef.current.downloadChart();
        }
    };

    const handleFilterReset = () => {
        setFiltersSubmitted(false);
        setShowChart(false);
    };

    if (loading) {
        return <div>≈Åadowanie danych...</div>;
    }

    if (error) {
        return <div>B≈ÇƒÖd: {error}</div>;
    }

    return (
        <div>
            <FiltersForm
                filters={filters}
                setFilters={setFilters}
                onFilterSubmit={() => setFiltersSubmitted(true)}
                fetchData={fetchData}
                onFilterReset={handleFilterReset}
                sortLabel="Sortowanie po cenie rosnƒÖco"
            />
            <div className="form-container">
                <p className="lead">Eksport danych</p>
                <div className="row justify-content-start">
                    <div className="col-6">
                        <button className="submit-button full-width" onClick={handleExportToXML}>Pobierz dane w formacie XML</button>
                    </div>
                    <div className="col-6">
                        <button className="submit-button full-width" onClick={handleExportToJSON}>Pobierz dane w formacie JSON</button>
                    </div>
                </div>
                <p></p>
                {filtersSubmitted && (
                    <div className="row justify-content-start">
                        <div className="col-6">
                            <button className="submit-button violet-btn full-width" onClick={() => setShowChart(!showChart)}>
                                {showChart ? 'Ukryj wykres' : 'Poka≈º wykres'}
                            </button>
                        </div>
                        {showChart && (
                            <div className="col-6">
                                <button className="submit-button violet-btn full-width" onClick={handleDownloadChart}>
                                    Pobierz wykres
                                </button>
                            </div>
                        )}
                    </div>
                )}
            </div>
            <div className="container">
                {showChart && (
                    <>
                        <DataChart data={pricesData} yLabel="≈örednia cena (z≈Ç)" downloadFileName="prices_chart" ref={pricesChartRef} />
                        <DataChart data={numbersData} yLabel="Liczba oddanych mieszka≈Ñ" downloadFileName="amounts_chart" ref={amountsChartRef} />
                    </>
                )}
                <table className="table">
                    <thead>
                        <tr>
                            <th>Region</th>
                            <th>Rok</th>
                            <th>Kwarta≈Ç</th>
                            <th>Typ mieszkania</th>
                            <th>Powierzchnia</th>
                            <th>≈örednia cena (z≈Ç)</th>
                            <th>Liczba oddanych mieszka≈Ñ</th>
                        </tr>
                    </thead>
                    <tbody>
                        {combinedData.map(item => (
                            <tr key={item.id}>
                                <td>{item.region}</td>
                                <td>{item.year}</td>
                                <td>{item.quarter}</td>
                                <td>{item.type}</td>
                                <td>{item.surface}</td>
                                <td>{item.price}</td>
                                <td>{item.amount}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default PricesData;
