import React, { useState, useEffect } from 'react';
import { request } from '../axios_helper';

const DataTable = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filters, setFilters] = useState({
        year: '',
        price: '',
        region: '',
        surface: '',
        type: '',
        before: '0',
        sort: '',
        sortAsc: '',
        oneYear: '0'
    });
    const wojewodztwa = [
        'DOLNOŚLĄSKIE', 'KUJAWSKO-POMORSKIE', 'LUBELSKIE', 'LUBUSKIE', 
        'ŁÓDZKIE', 'MAŁOPOLSKIE', 'MAZOWIECKIE', 'OPOLSKIE', 
        'PODKARPACKIE', 'PODLASKIE', 'POMORSKIE', 'ŚLĄSKIE', 
        'ŚWIĘTOKRZYSKIE', 'WARMIŃSKO-MAZURSKIE', 'WIELKOPOLSKIE', 'ZACHODNIOPOMORSKIE'
    ];

    const lata = Array.from({ length: 2022 - 2012 + 1 }, (_, i) => 2012 + i);

    const powierzchnie = [
        'do 40 m2', 'od 40,1 do 60 m2', 'od 60,1 do 80 m2', 'od 80,1 m2'
    ];
    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        console.log(filters);
        const queryParams = new URLSearchParams();
        Object.keys(filters).forEach(key => {
            if (filters[key]) {
                queryParams.append(key, filters[key]);
            }
        });
        request('GET', `/api/prices?${queryParams}`)
            .then(response => {
                console.log('Response data:', response.data); 
                setData(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.log('Error:', error);
                setError(error.message);
                setLoading(false);
            });
    };

    const handleFilterChange = (event) => {
        const { name, value } = event.target;
        setFilters({ ...filters, [name]: value });
    };

    const handleRadioChange = (event) => {
        const { name, value } = event.target;
        setFilters({ ...filters, [name]: value, before: '0', oneYear: '0', [name]: value });
    };

    const handleRadioClick = (event) => {
        const { name } = event.target;
        if (filters[name]) {
            setFilters({ ...filters, [name]: '' });
        }
    };
    const handleFilterSubmit = (event) => {
        event.preventDefault();
        setLoading(true);
        fetchData();
    };

    if (loading) {
        return <div>Ładowanie danych...</div>;
    }

    if (error) {
        return <div>Błąd: {error}</div>;
    }

    return (
        <div>

            <div className="container">

            <form onSubmit={handleFilterSubmit}>
                <div className="row">
                <label>
                    Sortowanie po cenie rosnąco:
                    <input
                        type="checkbox"
                        name="sortAsc"
                        checked={filters.sort === '1'}
                        onChange={(event) => handleFilterChange({ target: { name: 'sort', value: event.target.checked ? '1' : '' } })}
                    />
                </label>
                </div>

                <div className="row">
                <label>
                    Wybierz województwo:
                    <select name="region" value={filters.region} onChange={handleFilterChange}>
                        <option value="">Wszystkie</option>
                        {wojewodztwa.map(woj => (
                            <option key={woj} value={woj}>{woj}</option>
                        ))}
                    </select>
                </label>
                </div>

                <div className="row">
                <label>
                    Wybierz powierzchnię mieszkania:
                    <select name="surface" value={filters.surface} onChange={handleFilterChange}>
                        <option value="">Wszystkie</option>
                        {powierzchnie.map(sfc => (
                            <option key={sfc} value={sfc}>{sfc}</option>
                        ))}
                    </select>
                </label>
                </div>

                <div className="row">
                <div class="col-sm">
                <label>
                    Dane do roku
                    <input
                        type="radio"
                        name="yearFilter"
                        checked={filters.before === '1'}
                        value="1"
                        onChange={(event) => handleRadioChange({ target: { name: 'before', value: event.target.checked ? '1' : '0' } })}
                        onClick={handleRadioClick}

                    />
                </label>
                </div>
                <div class="col-sm"></div>
                <label>
                    Dane z roku
                    <input
                        type="radio"
                        name="yearFilter"
                        checked={filters.oneYear === '1'}
                        value="1"
                        onChange={(event) => handleRadioChange({ target: { name: 'oneYear', value: event.target.checked ? '1' : '0' } })}
                        onClick={handleRadioClick}

                    />
                </label>
                </div>
                <div class="col-sm">
                <label>
                    Wybierz rok:
                    <select name="year" value={filters.year} onChange={handleFilterChange}>
                        <option value="">Wszystkie</option>
                        {lata.map(rok => (
                            <option key={rok} value={rok}>{rok}</option>
                        ))}
                    </select>
                </label>
                </div>


                <button type="submit">Filtruj</button>
            </form>
            </div>

            <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Region</th>
                            <th>Kwartał</th>
                            <th>Typ mieszkania</th>
                            <th>Powierzchnia</th>
                            <th>Rok</th>
                            <th>Średnia cena</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map(item => (
                            <tr key={`${item.id}`}>
                                <td>{item.region}</td>
                                <td>{item.quarter}</td>
                                <td>{item.type}</td>
                                <td>{item.surface}</td>
                                <td>{item.year}</td>
                                <td>{item.price}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default DataTable;
