import * as React from 'react';
import WelcomeContent from './WelcomeContent';
import LoginForm from './auth/LoginForm';
import { request, setAuthHeader } from '../axios_helper';
import LoginErrorContent from './auth/LoginErrorContent';
import RegisterErrorContent from './auth/RegisterErrorContent';
import Navbar from './view/Navbar';

import Buttons from './Buttons';

export default class AppContent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            componentToShow: "welcome"
        }
    };

    login = () => {
        this.setState({ componentToShow: "login" });
    };

    logout = () => {
        this.setState({ componentToShow: "welcome" });
        setAuthHeader(null);
        localStorage.removeItem('token');
    };

    onLogin = (e, username, password) => {
        e.preventDefault();
        request(
            "POST",
            "/api/v1/auth/login",
            {
                login: username,
                password: password
            }).then(
                (response) => {
                    localStorage.setItem('token', response.data.token);
                    setAuthHeader(localStorage.getItem('token'));
                    this.setState({ componentToShow: "welcome" });
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "loginerror" });
                    }
                );
    };

    onRegister = (event, username, password) => {
        event.preventDefault();
        request(
            "POST",
            "/api/v1/auth/register",
            {
                login: username,
                password: password
            }).then(
                (response) => {
                    setAuthHeader(response.data.token);
                    this.setState({ componentToShow: "welcome" });
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "registererror" });
                    }
                );
    };

    render() {
        return (
            <>
                <Navbar login={this.login} logout={this.logout} />
                <p></p>
                <Buttons login={login} logout={logout} />

                {this.state.componentToShow === "welcome" && <WelcomeContent />}
                {this.state.componentToShow === "loginerror" && <LoginErrorContent />}
                {this.state.componentToShow === "registererror" && <RegisterErrorContent />}
                {this.state.componentToShow === "login" && <LoginForm onLogin={this.onLogin} onRegister={this.onRegister} />}
            </>
        );
    };
}
