import React, { useEffect, useState, forwardRef, useImperativeHandle } from 'react';
import Chart from 'chart.js/auto';

const PricesChart = forwardRef(({ pricesData, numbersData }, ref) => {
    const [chartInst, setChartInstance] = useState(null);

    useImperativeHandle(ref, () => ({
        downloadChart() {
            if (chartInst) {
                const link = document.createElement('a');
                link.href = chartInst.toBase64Image();
                link.download = 'prices_chart.png';
                link.click();
            }
        }
    }));

    useEffect(() => {
        if (pricesData.length > 0 || numbersData.length > 0) {
            const chartData = {};
            const keys = [...new Set([...pricesData.map(item => `${item.region} ${item.type} ${item.surface}`), 
                                     ...numbersData.map(item => `${item.region} ${item.type} ${item.surface}`)])];
            const colors = generateRandomColors(keys.length);

            pricesData.forEach(item => {
                const key = `${item.region}: ${item.type}, ${item.surface}`;
                if (!chartData[key]) {
                    chartData[key] = [];
                }
                chartData[key].push({ x: `${item.year} Q${item.quarter}`, y: item.price, surface: item.surface, dataset: 'prices' });
            });

            numbersData.forEach(item => {
                const key = `${item.region}: ${item.type}, ${item.surface}`;
                if (!chartData[key]) {
                    chartData[key] = [];
                }
                chartData[key].push({ x: `${item.year} Q${item.quarter}`, y: item.number, surface: item.surface, dataset: 'numbers' });
            });

            const ctx = document.getElementById('myChart').getContext('2d');
            const existingChart = Chart.getChart(ctx);
            if (existingChart) {
                existingChart.destroy();
            }

            const datasets = Object.keys(chartData).map((key, index) => {
                const color = colors[index];
                return {
                    label: key,
                    data: chartData[key],
                    backgroundColor: color,
                    borderColor: color.replace('0.5', '1'),
                    borderWidth: 1,
                    pointRadius: 5,
                    pointHoverRadius: 8,
                    showLine: true
                };
            });

            const chartInst = new Chart(ctx, {
                type: 'line',
                data: {
                    datasets: datasets
                },
                options: {
                    scales: {
                        x: {
                            title: {
                                display: true,
                                text: 'Rok i kwartał'
                            }
                        },
                        y: {
                            title: {
                                display: true,
                                text: 'Wartość'
                            },
                        }
                    },
                    plugins: {
                        tooltip: {
                            callbacks: {
                                label: function (context) {
                                    const dataPoint = context.raw;
                                    const valueType = dataPoint.dataset === 'prices' ? 'Cena' : 'Liczba';
                                    return `Powierzchnia: ${dataPoint.surface} m², ${valueType}: ${dataPoint.y}`;
                                }
                            }
                        }
                    }
                }
            });
            setChartInstance(chartInst);
        }
    }, [pricesData, numbersData]);

    const generateRandomColors = (numColors) => {
        const colors = [];
        for (let i = 0; i < numColors; i++) {
            const color = `rgba(${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)}, 0.5)`;
            colors.push(color);
        }
        return colors;
    };

    return (
        <div>
            <canvas id="myChart" width="400" height="400"></canvas>
        </div>
    );
});

export default PricesChart;
