import * as React from 'react';
import { request, setAuthHeader } from '../../axios_helper';
import LoginErrorContent from './LoginErrorContent';
import WelcomeContent from '../WelcomeContent';
import RegisterErrorContent from './RegisterErrorContent';
import Form from './Form';
class LoginForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            componentToShow: "form",
            active: "login",
            login: "",
            password: "",
            onLogin: props.onLogin,
            onRegister: props.onRegister
        };
    }


    onChangeHandler = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        this.setState({ [name]: value });
    }

    onSubmitLogin = (e) => {
        e.preventDefault();
        request(
            "POST",
            "/api/v1/auth/login",
            {
                login: this.state.login,
                password: this.state.password
            }).then(
                (response) => {
                    localStorage.setItem('token', response.data.token);
                    setAuthHeader(localStorage.getItem('token'));
                    this.props.onLogin(null, this.state.login, this.state.password);
                    this.setState({ componentToShow: "welcome" });

                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "loginerror" });
                    }
                );
    }

    onSubmitRegister = (e) => {
        e.preventDefault();
        request(
            "POST",
            "/api/v1/auth/register",
            {
                login: this.state.login,
                password: this.state.password
            }).then(
                (response) => {
                    localStorage.setItem('token', response.data.token);
                    setAuthHeader(localStorage.getItem('token'));
                    this.setState({ componentToShow: "welcome" });
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "registererror" });
                    }
                );
    }

    render() {
        const isLoginActive = this.state.active === "login";
        const submitButtonText = isLoginActive ? "Zaloguj się" : "Zarejestruj się";

        return (
            <>
                {this.state.componentToShow === "form" &&
                    <Form
                        active={this.state.active}
                        onChange={(value) => this.setState({ active: value })}
                        onSubmit={this.state.active === 'login' ? this.onSubmitLogin : this.onSubmitRegister}
                        onChangeHandler={this.onChangeHandler}
                    />}
                {this.state.componentToShow === "welcome" && <WelcomeContent />}
                {this.state.componentToShow === "loginerror" && <LoginErrorContent />}
                {this.state.componentToShow === "registererror" && <RegisterErrorContent />}
            </>
        );
    }
}
export default LoginForm;
