import React, { useEffect } from 'react';
import Chart from 'chart.js/auto';

const ChartComponent = ({ data }) => {
    useEffect(() => {
        if (data.length > 0) {
            const chartData = {};
            data.forEach(item => {
                const key = `${item.region}-${item.type}-${item.surface}`;
                if (!chartData[key]) {
                    chartData[key] = [];
                }
                chartData[key].push({ x: `${item.year} Q${item.quarter}`, y: item.price });
            });

            const ctx = document.getElementById('myChart').getContext('2d');
            const existingChart = Chart.getChart(ctx);
            if (existingChart) {
                existingChart.destroy();
            }

            const datasets = Object.keys(chartData).map(key => {
                const color = key.includes('pierwotny') ? 'rgba(255, 99, 132, 0.5)' : 'rgba(54, 162, 235, 0.5)';
                return {
                    label: key,
                    data: chartData[key],
                    backgroundColor: color,
                    borderColor: color.replace('0.5', '1'),
                    borderWidth: 1,
                    pointRadius: 5,
                    pointHoverRadius: 8,
                    showLine: true
                };
            });

            new Chart(ctx, {
                type: 'line',
                data: {
                    datasets: datasets
                },
                options: {
                    scales: {
                        x: {
                            title: {
                                display: true,
                                text: 'Rok i kwartał'
                            }
                        },
                        y: {
                            title: {
                                display: true,
                                text: 'Średnia ena'
                            },
                            beginAtZero: true
                        }
                    }
                }
            });
        }
    }, [data]);

    return (
        <div>
            <canvas id="myChart" width="400" height="200"></canvas>
        </div>
    );
};

export default ChartComponent;
